com.mirth.connect.plugins.javascriptrule.script:
var ignoreConfigurationException = false;

switch (sourceMap.get('contextPath') + '') {
	case '/getComponentMetaData':
		destination = 1;
		break;
	case '/getComponentDetails':
		destination = 2;
		break;
	case '/getComponentChildrenIds':
		destination = 3;
		break;
	case '/getConflictingComponents':
		destination = 4;
		break;
	case '/getConflicts':
		destination = 5;
		break;
	case '/compareComponent':
		destination = 6;
		break;
	case '/migrateComponents':
		destination = 7;
		break;
	case '/getEnvironments':
		destination = 8;
		break;
	case '/getSystems':
		destination = 9;
		break;
	case '/getConfiguration':
		destination = 10;
		ignoreConfigurationException = true;
		break;
	case '/setConfiguration':
		destination = 11;
		ignoreConfigurationException = true;
		break;
	case '/getReferencedCodeTemplates':
		destination = 12;
		break;
	case '/getVersion':
		destination = 13;
		ignoreConfigurationException = true;
		break;
	default:
		channelMap.put('reason', 'unsupported command');
		return false;
}

destinationSet.removeAllExcept(destination);
channelMap.put('ignoreConfigurationException', ignoreConfigurationException);

return true;

response = {};
const client = Packages.lu.hrs.mirth.migration.MirthMigrator;
var ignoreConfigurationException = channelMap.get('ignoreConfigurationException') == true;

// access headers
var headers = sourceMap.get('headers');
channelMap.put('org_sessionId', headers.getHeader('sessionId') || '');

// set default response status (OK)
var responseStatusCode = 200;

if(headers.contains('sessionId') && headers.getHeader('sessionId').trim()){
	var sessionId = headers.getHeader('sessionId');
	var configurationChanged = false;

	try {
		// determine configuration change state
		configurationChanged = client.hasConfigurationChanged(sessionId);  
	} catch (configurationException) {
		// there is not yet a valid configuration file, indicate it to the user 
		// if the requested service does not concern configuration handling
		if(!ignoreConfigurationException && (sessionId.indexOf('JSESSIONID') == 0)){
			// indicate that there is not yet a valid configuration (Unprocessable Content)
			channelMap.put('responseStatusCode', '500');
			response.message = 'No configuration file';
			responseMap.put('response', JSON.stringify(response));
			channelMap.put('reason', response.message);

			return false;
		}
	}

	// check if the configuration changed
	if(configurationChanged){
		responseStatusCode = 409;
		response.message = 'Configuration changed';

	} else if(sessionId.indexOf('JSESSIONID') == 0){
	// if an active session id was provided
		// check if it is still valid
		if(client.isValidUserSession(sessionId)){
			// it is valid, place the session id in the channel map
			channelMap.put('sessionId', headers.getHeader('sessionId'));
		} else{
			// indicate that session has expired (Login Timeout)
			responseStatusCode = 440;
			response.message = 'Session has expired';
		}
	}else{

		// client likes to instantiate a new session
		sessionId = client.createUserSession(sessionId);
		// if the session could be sucessfully established 
		if(sessionId){
			// place the session id in the channel map
			channelMap.put('sessionId', sessionId);
		} else{
			// indicate that credentials were invalid (Unauthorized)
			responseStatusCode = 401;
			response.message = 'Invalid authentication';
		}	
	}
} else{
	// indicate that there was no session id nor user credentials (Bad Request)
	responseStatusCode = 400;
	response.message = 'Missing authentication';
}

channelMap.put('responseStatusCode', responseStatusCode + '');

// in case of error, 
if(responseStatusCode > 399){
	if(responseStatusCode != 500){
		// remove the session id from the response
		channelMap.put('sessionId', '');
	}

	// set the response
	responseMap.put('response', JSON.stringify(response));
	channelMap.put('reason', response.message);

	return false;
}

return true;

